import { AbsoluteFileName } from 'ts-fs-utils';
import { EdgioFS } from '../edgio.fs';
export interface RuntimeOptions {
    /**
     * Indicates if the cache is enabled
     */
    isCacheEnabled: boolean;
    /**
     * Indicates if we're running a production build
     */
    isProductionBuild: boolean;
    /**
     * Indicates if we're running in development build
     */
    devMode: boolean;
    /**
     * The entry file for the application.
     * This can be either prod.js or dev.js entry file from the connector
     * depending on the mode we're running in.
     */
    entryFile: AbsoluteFileName;
    /**
     * The FS instance with snapshot of the file system structure
     * at the time of the runtime options creation
     */
    fs: EdgioFS;
    /**
     * The origins config for the local simulation
     */
    origins: any[];
    /**
     * The origins config for edge_control
     * when app is deployed
     */
    originsForEdgeControl: any[];
    /**
     * Indicates if the DRI logging is enabled
     */
    driLogging: boolean;
}
