/**
 * SecurityHeadersOptions
 */
export default interface SecurityHeadersOptions {
    /**
     * The `x-frame-options` header
     * indicates whether a browser should be allowed to render a page in a `<frame>`, `<iframe>`, `<embed>`, or `<object>`.
     * @see https://developer.mozilla.org/en-US/docs/Web/HTTP/Headers/X-Frame-Options
     * @default 'SAMEORIGIN'
     */
    xFrameOptions?: string | false;
    /**
     * The `x-xss-protection` header
     * indicates whether the browser should block a request when a cross-site scripting attack is detected.
     * @see https://developer.mozilla.org/en-US/docs/Web/HTTP/Headers/X-XSS-Protection
     * @default '1; mode=block'
     */
    xXssProtection?: string | false;
    /**
     * The `x-content-type-options` header
     * indicates that the MIME types advertised in the `Content-Type` headers should not be changed and be followed.
     * @see https://developer.mozilla.org/en-US/docs/Web/HTTP/Headers/X-Content-Type-Options
     * @default 'nosniff'
     */
    xContentTypeOptions?: string | false;
    /**
     * The `referrer-policy` header
     * indicates which referrer information should be included with requests made.
     * @see https://developer.mozilla.org/en-US/docs/Web/HTTP/Headers/Referrer-Policy
     * @default 'same-origin'
     */
    referrerPolicy?: string | false;
}
