/**
 * Adds Devtools script and style tags to the page <head> section
 *
 * IMPORTANT:
 * For now this module is not transpiled by tsc or babel
 * so it must be a valid nodeJS/browser file.
 *
 */
const EDGIO_DEVTOOLS_ASSETS_PATH = '/__edgio__/devtools/'
const EDGIO_DEVTOOLS_MAIN_JS = EDGIO_DEVTOOLS_ASSETS_PATH + 'main.js'
const EDGIO_DEVTOOLS_MAIN_CSS = EDGIO_DEVTOOLS_ASSETS_PATH + 'main.css'

const EDGIO_DEVTOOLS_COOKIE = 'edgio_devtools_enabled'
const EDGIO_DEVTOOLS_ENV_COOKIE = 'edgio_devtools_env_enabled'

function getCookieValue(cookieName) {
  const cookieContent = document.cookie
  if (cookieContent.indexOf(cookieName + '=') === -1) {
    return null
  }
  return cookieContent.indexOf(cookieName + '=true') >= 0
}

/**
 * Check if the Devtools is enabled, by order of priority
 * - per user own cookie set via GET /__edgio__/devtools/{enable,disable}
 * - per environment constibale config enable/disable
 * - enabled by default for moovweb and localhost hostnames
 * - disabled
 */
function isEnabled() {
  const userCookieValue = getCookieValue(EDGIO_DEVTOOLS_COOKIE)

  if (userCookieValue !== null) {
    return userCookieValue
  }

  const envCookieValue = getCookieValue(EDGIO_DEVTOOLS_ENV_COOKIE)

  if (envCookieValue !== null) {
    return envCookieValue
  }

  if (
    /127\.0\.0\.1|^localhost(:\d+)?$|moovweb-layer0(-dev)?\.io$|edgio-dev|layer0(-dev|-limelight|-limelight-dev)?(-perma)?\.link$|edgio(-perma)?\.link$/.test(
      window.location.host
    )
  ) {
    return true
  }

  return false
}

function install() {
  if (typeof window === 'undefined') {
    // This should not execute on SSR
    return
  }

  if (!isEnabled()) {
    return
  }

  if (document.head.querySelectorAll('script[src="' + EDGIO_DEVTOOLS_MAIN_JS + '"]').length) {
    // Devtools is already mounted
    return
  }

  const script = document.createElement('script')
  const link = document.createElement('link')

  script.defer = true
  script.src = EDGIO_DEVTOOLS_MAIN_JS

  link.rel = 'stylesheet'
  link.href = EDGIO_DEVTOOLS_MAIN_CSS

  document.head.appendChild(script)
  document.head.appendChild(link)
}

module.exports = install
module.exports.default = install
